import React from 'react';
import './App.css';

import { Map, Marker, Popup, TileLayer } from 'react-leaflet'

// CONSTANTS
import axios from 'axios';



import L from 'leaflet';

delete L.Icon.Default.prototype._getIconUrl;

L.Icon.Default.mergeOptions({
    iconRetinaUrl: require('leaflet/dist/images/marker-icon-2x.png'),
    iconUrl: require('leaflet/dist/images/marker-icon.png'),
    shadowUrl: require('leaflet/dist/images/marker-shadow.png')
});

const ENDPOINT = 'http://localhost:8000';

function createIconImgUrl(icon){
  return `http://openweathermap.org/img/w/${icon}.png`
}

function convertKtoF(kelvin){
  return Math.round(9 /  5 * (kelvin - 273) + 32, 2)
}


function unwrap({stationID, stationInformation}){
  const reducedStationInfo= {
    coordinates:  [stationInfo.geometry.coordinates[1], stationInfo.geometry.coordinates[0]],
    id: stationInfo.properties.kioskId,
    name: stationInfo.properties.name,
    addressStreet: stationInfo.properties.addressStreet,
    bikesAvailable:stationInfo.properties.bikesAvailable,
    totalDocks:stationInfo.properties.totalDocks,
  }
  return {[stationID]: reducedStationInfo}
}

class AppContainer extends React.Component{
  constructor(props){
    super(props);
    this.state = {
      stations: [],
      weather: {},
    };
    this.handleClickOnMarker = this.handleClickOnMarker.bind(this)
    this.handleClickOnStationInfo = this.handleClickOnStationInfo.bind(this)
  }



  componentDidMount(){
    axios.get(ENDPOINT)
        .then(response => response.data)
        .then(results => {
          const stations = Object.values(results.stations).map(item => ({
                coordinates:  [item.geometry.coordinates[1], item.geometry.coordinates[0]],
                id: item.properties.kioskId,
                name: item.properties.name,
                addressStreet: item.properties.addressStreet,
                bikesAvailable:item.properties.bikesAvailable,
                totalDocks:item.properties.totalDocks,
              }));
          const weather = {
              temperature: convertKtoF(results.weather.main.temp) + '\xB0',
              iconUrl: createIconImgUrl(results.weather.weather[0].icon),
              weather: results.weather.weather[0].main,};
          this.setState({stations, weather});
        })
  }

  handleClickOnMarker(){
    this.setState({


    });
  }

  handleClickOnStationInfo(){
    this.setState({

    });

  }



  render(){
    return (
      <div>

    <Weather weather={this.state.weather}/>
    <StationsContainer stations={this.state.stations}/>
    </div>
    )}
}


function Weather(props){
  return (
      <div className="weather">
          <span>{props.weather.temperature}</span>
          <span><img src={props.weather.iconUrl} alt="weather icon"></img></span>
          <span>{props.weather.weather}</span>
      </div>
  )}


const position = [39.95378, -75.16374];
const zoom = 13;
const mapStyle = {
    height: 300,
    width: 400,
}

const accessToken = `pk.eyJ1IjoiZWxsaW90Z2ZyYW5rIiwiYSI6ImNqd3V4aXFzZTBkMjA0YW4xNGFwNnU4cWwifQ.BsUA_68pUHaCU7v4PCKLKw`
const id = 'mapbox.streets';
const mapboxURL = `https://api.tiles.mapbox.com/v4/${id}/{z}/{x}/{y}.png?access_token=${accessToken}`

class StationsContainer extends React.Component{
  constructor(props){
      super(props);
  }

  render(){
    return (
      <div>
          <Map center={position} zoom={zoom} style={mapStyle}>
          <TileLayer
              url={mapboxURL}
              attribution="&copy; <a href=&quot;http://osm.org/copyright&quot;>OpenStreetMap</a> contributors"/>
          <MarkersList stationsList={this.props.stations}/>
          </Map>
          <h2>Stations</h2>
          <StationsList stationsList={this.props.stations}/>

      </div>
  )}
}

function StationsList(props){

  let stations = props.stationsList
                      .map(s => <Station key={s.id}  station={s} />);
  return stations;
}  

function MarkersList(props){
  let markersArray = props.stationsList.map(s => <StationMarker key={`marker-${s.id}`} station={s} />);
  return markersArray;
}



function StationMarker(props){
  return (
    <Marker position={props.station.coordinates} id={props.station.id}>
      <Popup> <h1>{props.station.name}</h1></Popup>
    </Marker>
    )

}
class Station extends React.Component{
  constructor(props){
    super(props);
    this.state = {
      isExtended: false,
    }
    this.toggleExtendedInfo = this.toggleExtendedInfo.bind(this);
  }

  toggleExtendedInfo(){
      this.setState({
        isExtended: !this.state.isExtended,
      });

    }


  render(){
  return (
    <div key={this.props.station.id} >
      <h1 onClick={this.toggleExtendedInfo}>{this.props.station.name}</h1>
    {this.state.isExtended && <div className="extended-info">
            {this.props.station.addressStreet}<div/>
            <h3>{this.props.station.bikesAvailable} / {this.props.station.totalDocks}</h3>
          </div>}
    </div>
    )}
}





function App() {
  return (
    <div className="App">
      <AppContainer/>
    </div>
  );
}



export default App;
