import fetch from 'cross-fetch'

export const CLICK_MARKER = 'CLICK_MARKER'

export const EXPLORE_PANEL = 'EXPLORE_PANEL'

export const SEARCH_PANEL = 'SEARCH_PANEL'

export const CLICK_PANEL = 'CLICK_PANEL'
// Action creators

export const REQUEST_API_DATA = 'REQUEST_API_DATA'
export const FETCH_API_DATA_SUCCESS = 'FETCH_API_DATA_SUCCESS'
export const FETCH_API_DATA_FAILURE = 'FETCH_API_DATA_FAILURE'

export const RECEIVE_API_DATA = 'RECEIVE_API_DATA'

// export const FETCH_GEODATA_REQUEST = 'FETCH_GEODATA_REQUEST'
// export const FETCH_GEODATA_SUCCESS = 'FETCH_GEODATA_SUCCESS'
// export const FETCH_GEODATA_FAILURE = 'FETCH_GEODATA_FAILURE'

export function clickMarker(id, isSelected){
	return {
		type: CLICK_MARKER,
		id,
		isSelected
	}
}

export function clickPanel(panel){
	return {
		type: CLICK_PANEL,
		panel
	}
}



//  Network Actions

export function requestAPIData(){
	return {
		type: REQUEST_API_DATA
	}
}

export function receiveAPIData(json){
	return {
		type: RECEIVE_API_DATA,
		bikeTimestamp: json.timestamp,
		stationsByID: json.stations,
		weather: json.weather,
	}
}

export function fetchAPIData(dispatch){
	dispatch(requestAPIData);
	return fetch('https://bike-philly.herokuapp.com/api/v1/stations')
	  .then(
        response => response.json(),
        error => console.log('An error occurred.', error)
      )
      .then(json => dispatch(receiveAPIData(json)))

}
