import React, {Component} from 'react';
import logo from './logo.svg';
import './App.css';



// CONSTANTS

const BIKEURL = 'https://www.rideindego.com/stations/json/';
const WEATHERURL = createWeatherUrl();


function createWeatherUrl(){
  let baseUrl = 'http://api.openweathermap.org/data/2.5/weather',
      city = 'Philadelphia',
      country = 'us',
      accessToken = 'd795b96fd86f14f35c5a2e8d779bbd6a';
    return `${baseUrl}?q=${city},${country}&APPID=${accessToken}`;
}

function createIconImgUrl(icon){
  return `http://openweathermap.org/img/w/${icon}.png`
}

function convertKtoF(kelvin){
  return Math.round(9 /  5 * (kelvin - 273) + 32, 2)
}



class Weather extends React.Component{
  constructor(props){
    super(props);
    this.state = {
      temperature: '',
      iconUrl: '',
      weather: '',
    };
  }

  componentDidMount(){
    fetch(WEATHERURL)
        .then(response => response.json())
        .then((results) => this.setState({
      temperature: convertKtoF(results.main.temp) + '\xB0',
      iconUrl: createIconImgUrl(results.weather[0].icon),
      weather: results.weather[0].main,
    })
  )}

  render() {
    return (
      <div className="weather">
          <span>{this.state.temperature}</span>
          <span><img src={this.state.iconUrl} alt="weather icon"></img></span>
          <span>{this.state.weather}</span>
      </div>
  )}
}

class StationList extends React.Component{
  constructor(props){
      super(props);
      this.state = {
        stationsList: [],
        isOrdered: false,
      };
  }

  componentDidMount(){
    fetch(BIKEURL)
        .then(response => response.json().features)
        .then(
      function (data){
        console.log(data);
        console.log(typeof data);
