import React from 'react';
import { Marker as LeafletMarker, Popup as LeafletPopup} from 'react-leaflet'
import L from 'leaflet';

delete L.Icon.Default.prototype._getIconUrl;

L.Icon.Default.mergeOptions({
    iconRetinaUrl: require('leaflet/dist/images/marker-icon-2x.png'),
    iconUrl: require('leaflet/dist/images/marker-icon.png'),
    shadowUrl: require('leaflet/dist/images/marker-shadow.png')
});


const blueIcon = new L.Icon({
  iconUrl: 'https://cdn.rawgit.com/pointhi/leaflet-color-markers/master/img/marker-icon-blue.png',
  shadowUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/0.7.7/images/marker-shadow.png',
  iconSize: [25, 41],
  iconAnchor: [12, 41],
  popupAnchor: [1, -34],
  shadowSize: [41, 41]
});

const yellowIcon = new L.Icon({
  iconUrl: 'https://cdn.rawgit.com/pointhi/leaflet-color-markers/master/img/marker-icon-yellow.png',
  shadowUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/0.7.7/images/marker-shadow.png',
  iconSize: [25, 41],
  iconAnchor: [12, 41],
  popupAnchor: [1, -34],
  shadowSize: [41, 41]
});

const greyIcon = new L.Icon({
  iconUrl: 'https://cdn.rawgit.com/pointhi/leaflet-color-markers/master/img/marker-icon-grey.png',
  shadowUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/0.7.7/images/marker-shadow.png',
  iconSize: [25, 41],
  iconAnchor: [12, 41],
  popupAnchor: [1, -34],
  shadowSize: [41, 41]
});

const POPUP_STYLE = {
  background: '#2c3e50',
  color: '#fff',
  fontSize: '16px',
  lineHeight: '24px',
}

const FLEX_CONTAINER = {
  display: 'flex',
  justifyContent: 'center',
  alignContent: 'center',
}

const FLEX_ITEM = {
  flexBasis: '100%',
  textAlign: 'center',
}


const Marker = ({ name, bikesAvailable, docksAvailable, coordinates, onClick, active, searched } ) => {
  let icon = blueIcon
  if ( !!active ) {
     icon = yellowIcon;
  } else if ( !!searched ) {
     icon = greyIcon;
  }


  return (

	<LeafletMarker 
		position={coordinates}
		onClick={onClick}
		icon = { icon }
	>
		<LeafletPopup className={'custom-popup'}>
		<h3>{name}</h3>
    <div style={FLEX_CONTAINER}>
      <div style={FLEX_ITEM}>
      <h2>{bikesAvailable}</h2>
      <p>bikes</p>

      </div>
      <div style={{borderLeft: '5px solid #f5f5f5'}}/>
      <div style={FLEX_ITEM}>
      <h2>{docksAvailable}</h2>
      <p>spots available</p>
      </div>

    </div>
		 </LeafletPopup>
	</LeafletMarker>
  )
}

export default Marker
