import { connect } from 'react-redux'
import React from 'react';
import { fetchDistanceMatrix } from '../actions'

class SearchBar extends React.Component{
	constructor(props){
		super(props)
		this.state = {
			input: ''
		}
	    this.handleChange = this.handleChange.bind(this);
	    this.handleSubmit = this.handleSubmit.bind(this);

	}


	handleChange(event){
		this.setState({
    		input: event.target.value
    	});
	}


	handleSubmit(){
		let coordinateSubmission = [this.state.input.split(',').map(x => +x)];
		let coordinateArray = coordinateSubmission.concat(this.props.coordinateReference);
		this.props.searchForDistanceMatrix(coordinateSubmission)
	}

	render() {
		return (
			<div>
				<h2>Type in a new Message:</h2>
				<input
				value={this.state.input}
				onChange={this.handleChange}
				style={{border: '1px solid black'}}/><br/>
				<button onClick={this.handleSubmit}>Submit</button>
			</div>
		)
	}
}




const mapStateToProps = (state) => {
  return {
  	coordinateReference: Object.values(state.api.stationsByID).map(x => x.coordinates),
  	}
};

const mapDispatchToProps = (dispatch) => {
  return {
    searchForDistanceMatrix: (coordinateArray) => {
      	dispatch(fetchDistanceMatrix(coordinateArray))
    }
  }
};


const SearchBarWrapper = connect(mapStateToProps, mapDispatchToProps)(SearchBar);


export default SearchBarWrapper;
