import { combineReducers } from 'redux'
import { CLICK_MARKER, 
		 UNCLICK_MARKER,
		 EXPLORE_PANEL,
		 CLICK_PANEL,
		 SEARCH_PANEL } from './actions'

// UI Reducers



function copyAndRemove(array, index){
	// Copies an array without the element at the index provided
	return [...array.slice(0,index), ...array.slice(index+1)]
}

function panel(state=EXPLORE_PANEL, action){
	switch (action.type){
		case CLICK_PANEL:
			return action.panel
		default:
			return state
	}
}

function clickedStations(state=[], action){
	switch (action.type) {
		case CLICK_MARKER:
			return [...state, action.id]

		case UNCLICK_MARKER:
			let markerIndex = state.indexOf(action.id);
			return copyAndRemove(state, markerIndex)

		default:
			return state
	}
}


const stationsJSON = {
	3004: {
		coordinates: [-75.16374, 39.95378],
		name: 'First station',
		address: 'First street',
		bikes: 20,
		spots: 2
	},
	3005: {
		coordinates: [], 
		name: 'Second station',
		address: 'Second street',
		bikes: 10,
		spots: 2
	}
}

function stationsByID(state=stationsJSON, action){
	return state
}

const reduceApp = combineReducers({
  panel,
  clickedStations,
  stationsByID
})


