import React from 'react';
import './App.css';

import { Map, Marker, Popup, TileLayer } from 'react-leaflet'

// CONSTANTS
import axios from 'axios';
const ENDPOINT = 'http://localhost:8000';


function createIconImgUrl(icon){
  return `http://openweathermap.org/img/w/${icon}.png`
}

function convertKtoF(kelvin){
  return Math.round(9 /  5 * (kelvin - 273) + 32, 2)
}



class Weather extends React.Component{
  constructor(props){
    super(props);
    this.state = {
      temperature: '',
      iconUrl: '',
      weather: '',
    };
  }

  componentDidMount(){
    axios.get(ENDPOINT)
        .then(response => response.data.weather)
        .then((results) => this.setState({
      temperature: convertKtoF(results.main.temp) + '\xB0',
      iconUrl: createIconImgUrl(results.weather[0].icon),
      weather: results.weather[0].main,
    })
  )}

  render() {
    return (
      <div className="weather">
          <span>{this.state.temperature}</span>
          <span><img src={this.state.iconUrl} alt="weather icon"></img></span>
          <span>{this.state.weather}</span>
      </div>
  )}
}
const position = [39.95378, -75.16374];
const zoom = 13;
const mapStyle = {
    height: 300,
    width: 400,
}

const accessToken = `pk.eyJ1IjoiZWxsaW90Z2ZyYW5rIiwiYSI6ImNqd3V4aXFzZTBkMjA0YW4xNGFwNnU4cWwifQ.BsUA_68pUHaCU7v4PCKLKw`
const id = 'mapbox.streets';
const mapboxURL = `https://api.tiles.mapbox.com/v4/${id}/{z}/{x}/{y}.png?access_token=${accessToken}`

class StationsList extends React.Component{




}

class StationsContainer extends React.Component{
  constructor(props){
      super(props);
      this.state = {
        stationsList: [],
        isOrdered: false,
      };
  }

  componentDidMount(){
    axios.get(ENDPOINT)
        .then(response => Object.values(response.data.stations))
        .then(data => {
          const stationsList = data.map(item => ({
                coordinates:  [item.geometry.coordinates[1], item.geometry.coordinates[0]],
                id: item.properties.kioskId,
                name: item.properties.name,
                addressStreet: item.properties.addressStreet,
                bikesAvailable:item.properties.bikesAvailable,
                totalDocks:item.properties.totalDocks,
              }));
        this.setState({ stationsList })
    })
  }

  render(){
    const stations = this.state.stationsList
                      .map(s => <Station key={s.id} station={s} />);
    return (
      <div>
          <Map center={position} zoom={zoom} style={mapStyle}>
          <TileLayer
              url={mapboxURL}
              attribution="&copy; <a href=&quot;http://osm.org/copyright&quot;>OpenStreetMap</a> contributors"/>
          <MarkersList stationsList={this.props.stationsList}/>
          </Map>
          <h2>Stations</h2>
          {stations}

      </div>
  )}
}


class Station extends React.Component{
  constructor(props){
    super(props);
    this.state = {
      isExtended: false,
    }
    this.toggleExtendedInfo = this.toggleExtendedInfo.bind(this);
  }

  toggleExtendedInfo(){
    console.log(this.state);
      this.setState({
        isExtended: !this.state.isExtended,
      });

    }


  render(){
  return (
    <div key={this.props.station.id} >
      <h1 onClick={this.toggleExtendedInfo}>{this.props.station.name}</h1>
    {this.state.isExtended && <div className="extended-info">
            {this.props.station.addressStreet}<div/>
            <h3>{this.props.station.bikesAvailable} / {this.props.station.totalDocks}</h3>
          </div>}
    </div>
    )}
}





function App() {
  return (
    <div className="App">
      <Weather/>
      <StationsContainer/>
    </div>
  );
}

export default App;
