    //     });
    //     console.log(stationsList);
    //     this.setState({
    //         stationsList,
    // });
  })}

  render(){
    const stations = this.state.stationsList
        .map((s)=><li key={s.id}>{s.name}</li>);
    return (
      <div>
          <h2>Stations</h2>
          <ul>{stations}</ul>
      </div>



  )}
}

function App() {
  return (
    <div className="App">
      <Weather/>
      <StationList/>
    </div>
  );
}

export default App;
